function Bootstrap() {}

Bootstrap.prototype.label = function(data) {
    return '<span class="label pull-right ' + data.cls + '">' + data.text + '</span>';
}

Bootstrap.prototype.pageHeader = function(data) {
    var html = '<div class="page-header">';
    html += '<h2>';
    html += data.title;
    if (data.subTitle) {
        html += ' <small>' + data.subTitle + '</small>';
    }
    if (data.label) {
        html += Bootstrap.prototype.label(data.label);
    }
    html += '</h2>';
    html += '</div>';
    return html;
}

Bootstrap.prototype.well = function(data) {
    return '<div class="well">'+data+'</div>';
}

Bootstrap.prototype.table = function(data) {
    var firstRow,
        html = '<table class="bordered-table zebra-striped">',
        l = data.length;

    if (l) {
        // header
        html += '<thead><tr>';
        firstRow = data[0];
        for (var key in firstRow) {
            html += '<th>' + key + '</th>';
        }
        html += '</tr></thead>';

        // rows
        html += '<tbody>';
        for (var i = 0; i < l; i++) {
            html += '<tr>';
            for (var key in data[i]) {
                html += '<td>' + data[i][key] + '</td>';
            }
            html += '</tr>';
        }
        html += '</tbody>';

    }

    html += '</table>';
    return html;
}

